import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './News.css'
import NewsCard from './NewsCard';

const News = () => {

  const [articles, setArticles] = useState([])
  useEffect(() => {
    axios.get('http://localhost:8000/article')
      .then(res => {
        setArticles(res.data)
      })
      .catch(err => {
        console.log(err);
      })
  }, []);



