import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './News.css'
import NewsCard from './NewsCard';

const News = () => {

  const [articles, setArticles] = useState([])
  useEffect(() => {
    axios.get('http://localhost:8000/article')
      .then(res => {
        setArticles(res.data)
      })
      .catch(err => {
        console.log(err);
      })
  }, []);


  const [categories, setCategories] = useState([])
  useEffect(() => {
    axios.get('http://localhost:8000/article/categorie')
      .then(res => {
        console.log(res);
        setCategories(res.data)
      })
      .catch(err => {
        console.log(err);
      })
  }, []);

// const chooseCategorie = (e) => {
//   let categorieChoose = categories.filter(categorie => {
//     categorie.cat_name === e.target.value
//   })
//   let newCatId = categorieChoose[0].categorie_id
//   setArticles({...articles, [e.target.name, e.target]})
// };


  return (
    <div className='container mx-auto'>
        <label className='text-secondary mt-3'>Sélectionnez une catégorie</label>
      <select className="browser-default custom-select mb-5 ">
        {categories && categories.map(categorie => {
          return (<option>{categorie.cat_name}</option>)
        })}
      </select>

      <div className='row'>
        {articles && articles.map((article) => {
          return (<NewsCard article={article} />)
        }
        )}
      </div>
    </div>
  );
}

export default News;
