import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './News.css'
import NewsCard from './NewsCard';

const News = () => {

const [articles, setArticles] = useState([])
const getArticles = async () => {
  const result = await
    axios.get('http://localhost:8000/article')
        setArticles(result.data)
}  

useEffect(() => {
  getArticles()
},[])

// const articlebyCat = (e) => {
//     articles.filter((article, i) => {
//       // article.cat_name === e.target.value
//       console.log(article.cat_name)
//     })

//   }




  const articlebyCat = (e) => {articles.filter(catArticle => {
      if(catArticle.cat_name === e.target.value) {
        setArticles(articlebyCat)
      }
    })}
    console.log('====================================');
    console.log(articlebyCat);
    console.log('====================================');




  const [categories, setCategories] = useState([])
  useEffect(() => {
    axios.get('http://localhost:8000/article/categorie')
      .then(res => {
        console.log(res);
        setCategories(res.data)
      })
      .catch(err => {
        console.log(err);
      })
  }, []);


  return (
    <div className='container mx-auto'>
        <label className='text-secondary mt-3'>Sélectionnez une catégorie</label>

      <select className="browser-default custom-select mb-5 " onChange={(e)=>articlebyCat(e)} >
        {categories && categories.map(categorie => {
          return (<option>{categorie.cat_name}</option>)
        })}
      </select>

      <div className='row'>
        {articles && articles.map((article) => {
          return (<NewsCard article={article} />)
        }
        )}
      </div>
    </div>
  );
}

export default News;
