import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './News.css'
import NewsCard from './NewsCard';

const [articles, setArticles] = useState([])
const News = async () => {
  const result = await
    axios.get('http://localhost:8000/article')
        setArticles(result.data)


useEffect(() => {
  News()
},[])

// const articlebyCat = async (e) => {

//   // const newArticles = articles.filter(catArticle => {
//     //   catArticle.cat_name === e.target.value
//     // })
//     // setArticles(newArticles)
//   }
//   setTimeout(function(){ console.log(articles[1].date)}, 3000)




  const [categories, setCategories] = useState([])
  useEffect(() => {
    axios.get('http://localhost:8000/article/categorie')
      .then(res => {
        console.log(res);
        setCategories(res.data)
      })
      .catch(err => {
        console.log(err);
      })
  }, []);


  // const chooseCategorie = () => {

  // }


  return (
    <div className='container mx-auto'>
        <label className='text-secondary mt-3'>Sélectionnez une catégorie</label>

      <select className="browser-default custom-select mb-5 " >
        {categories && categories.map(categorie => {
          return (<option>{categorie.cat_name}</option>)
        })}
      </select>

      <div className='row'>
        {articles && articles.map((article) => {
          return (<NewsCard article={article} />)
        }
        )}
      </div>
    </div>
  );
}

export default News;
